import { useEffect } from "react";
import { SliderCards } from "../components";
import { sliderItems } from "../constants";




const HomeSectionFour = () => {
    
        

      
    useEffect(() => {
        var scrollWheel = document.getElementById('autoScroll');
        var interval = 4000;
        var animationPaused = false;
        
        function slideLeft() {
            setTimeout(slideRight, interval);
          
            if (animationPaused)
              return;
        };

        function scrollFunction() {           
            scrollWheel.classList.add('s-4-anim');
        };

        scrollWheel.addEventListener('mouseover', function(e) {
            animationPaused = true;
        });
        
        scrollWheel.addEventListener('mouseout', function(e) {
            animationPaused = false;
        });
    
        scrollFunction();
    }, []);
        

    return (
        <section className="home-section-four">
            <div className="h-container-4 container">
                <div className="flex">
                    <div className="w-5/12 text-8xl/tighter font-semibold -tracking-tighten p-2.5">
                        <h2 className="mt-14 mb-10 text-sky-950/100">You name it, weâ€™ve figured it out.</h2>
                    </div>
                </div>
            </div>


            <div className="slider-wrapper">
                <div id="autoScroll" className="slides-wrap">
                    <div className="slides">
                        {
                            sliderItems.map((item) => {
                                return (
                                    <SliderCards key={item.label} {...item} />
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </section>
    );
};

export default HomeSectionFour;
