import { useEffect, useState } from "react";
import { customersQuotes, customerThumbnail } from "../constants";
import { TestimonialQuotes, TestimonialThumbnail } from "../components";
import { VideoIcon } from "../assets/icons";
import { wura } from "../assets/images";






const HomeSectionOne = () => {

    const [activeImage, setActiveImage] = useState(wura);
    console.log("Active Thumbnail: ", activeImage);
    
    const [customersQuote, setCustomersQuote] = useState(customersQuotes);
    console.log("Active Customer Quote: ", customersQuote);


    useEffect(() => {
        if (activeImage.length !== customersQuote.length) {
            setCustomersQuote(activeImage.length);
        }
    }, []);


    return (
        <section className="absolute top-0 py-24 px-0">
            <div className="px-7 w-full mx-auto container">
                <div className="flex justify-between py-20 px-0 space-x-12">


                    <div className="relative home-section-one--left">
                        <div className="customers-testimonials">

                            <div className=" w-127 testimonial">
                                <div className="absolute testimonial-backdrop"></div>
                                <div className="testimonial-video-ctrl"><VideoIcon /></div>
                                {
                                    customersQuotes.map((item) => {
                                        return (
                                            <TestimonialQuotes key={item.textAuthor} {...item} />
                                        );
                                    })
                                }
                                <div className="bg-white aboslute testimonial-overlay"></div>
                                <div className="relative h-126 w-124 testimonial-media">
                                    <img className="" src={activeImage} alt="customer" />
                                </div>
                            </div>


                        </div>
                        <ul className="flex absolute -bottom-100 testimonials-thumbnail">
                            {
                                customerThumbnail.map((item) => {
                                    return (
                                        <li key={item} className="w-16 h-20 my-5 mr-8 cursor-pointer relative">
                                            <TestimonialThumbnail
                                                exactItem={item}
                                                activeImage={activeImage}
                                                changeActiveImage={(item) => setActiveImage(item)}
                                            />
                                        </li>
                                    );
                                })
                            }
                        </ul>
                    </div>

                    
                    <div className="home-section-one--right">

                    </div>


                </div>
            </div>
        </section>
    )
}

export default HomeSectionOne
