import { useState } from "react";
import { flipItems } from "../constants";
import { Disqus } from "../components";
import { useEffect } from "react";








const HomeSectionSeven = () => {


    console.clear();


    const [itemToFlip, setItemToFlip] = useState(flipItems);
    console.log('Flip Item: ', itemToFlip);


    
    useEffect(() => {
        function loopElementFunction() {
            for (var n = 0; n < itemToFlip.length; n++) {
                console.log('Active Number: ', n);
                console.log('Item to Flip: ', itemToFlip[n]);


                setInterval(() => {
                    var disqusID = document.getElementById('disqosWrap');
                    var disqus = disqusID.getElementsByClassName('disqus');

                    for (var i = 0; i < disqus.length; i++) {
                        console.log('Active Flip Number: ', i);
                        console.log('Flip Item: ', disqus[i] = disqus[i].classList.add('is-active'));
                        console.log('Flip Item: ', disqus[i] = disqus[i].classList.remove('hidden'));

                        if (n === i) {
                            i =+ i;
                            console.log('Flip Item: ', disqus[i]); // Print the current item
                        } else {
                            return 0;
                        }
                    }
                }, 30000); // 30 seconds in milliseconds

            }
        }
        loopElementFunction();
    }, []);

    




    return (
        <section className="home-section-seven">
            <div className="h-container-7 container">
                <div className="h-section-7-wrap">
                    <h6>why cowrywise?</h6>
                    <div id="disqosWrap" className="relative">
                        {
                            flipItems.map((item) => {
                                return (
                                    <Disqus key={item.title} {...item} />
                                );
                            })
                        }
                    </div>
                </div>
            </div>
        </section>
    );
};
export default HomeSectionSeven;
