import { useState } from "react";
import { flipItems } from "../constants";
import { Disqus } from "../components";
import { useEffect } from "react";



const HomeSectionSeven = () => {

    console.clear();

    const [flipItem, setFlipsItem] = useState(flipItems);
    console.log('Flip Items: ', flipItem);
    





    useEffect(() => {
        setInterval(() => {
            for (var n = 0; n < flipItem.length; n++) {
                console.log('Active Number: ', n);
                console.log('Active Flip Item: ', flipItem[n]);
                  
                n = n + 1;
                console.log('Current Index: ', n);
            }
        }, 1000);
            
            // var disqosElements = document.getElementById('disqosWrap').getElementsByClassName('disqus');
            //     console.log('Found Discuss Items: ', disqosElements);
                
            //     for (var i = 0; i < disqosElements.length; i++) {
            //         if (n === i) {
            //             console.log('Found Discuss Items 1: ', disqosElements[n]);
            //         } else {
            //             console.log('Found Discuss Items 2: ', disqosElements[n]);
            //         }
            //     }
    }, []);


    return (
        <section className="home-section-seven">
            <div className="h-container-7 container">
                <div className="h-section-7-wrap">
                    <h6>why cowrywise?</h6>
                    <div id="disqosWrap" className="relative">
                        {
                            flipItems.map((item) => {
                                return (
                                    <Disqus key={item.title} {...item} />
                                );
                            })
                        }
                    </div>
                </div>
            </div>
        </section>
    );
};
export default HomeSectionSeven;
