import {useRef, useEffect } from "react";
import { Utils } from "../utils";
import { HomeSectionOne, HomeSectionTwo, HomeSectionThree, HomeSectionFour, HomeSectionFive, HomeSectionSix, HomeSectionSeven, } from "../sections";




const HomeBody = () => {

    const elementRef = useRef(null);

    useEffect(() => {
        function handleScroll() {
            if (Utils.isElementInView(elementRef.current, true)) {
                console.log('Element is fully in view!');
            } else {
                console.log('Element is not fully in view!');
            }
        }

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);


    return (
        <main className="absolute top-0 w-full">
            <HomeSectionOne ref={elementRef} />
            <HomeSectionTwo ref={elementRef} />
            <HomeSectionThree ref={elementRef} />
            <HomeSectionFour ref={elementRef} />
            <HomeSectionFive ref={elementRef} />
            <HomeSectionSix ref={elementRef} />
            <HomeSectionSeven ref={elementRef} />
        </main>
    );
};
export default HomeBody;
