import { useState } from "react";
import { flipItems } from "../constants";
import { Disqus } from "../components";
import { useEffect } from "react";








const HomeSectionSeven = () => {



    const [initialState, setInitialState] = useState(0);
    console.log('At Initial State: ', initialState);



    const [itemToFlip, setItemToFlip] = useState(flipItems);
    console.log('Flip Item: ', itemToFlip);
    


    
    useEffect(() => {
        function flipInfoFunction() {
            for (var n = 0; n < itemToFlip.length; n++) {
                console.log('Active Number: ', n);
                console.log('Active Flip Item: ', itemToFlip[n]);
                
                    
                var disqusID = document.getElementById('disqosWrap');
                var flipItem = disqusID.getElementsByClassName('disqus');
                console.log('Found Discuss Items: ', flipItem);
                

                for (var i = 0; i < flipItem.length; i++) {
                    console.log('Found Discuss Items 1: ', flipItem[i]);

                    n === i ?
                        setInterval(() => {
                            setTimeout(() => {
                                flipItem[i].classList.add('is-active');
                                flipItem[i].classList.remove('hidden');

                                setTimeout(() => {
                                    flipItem[i].classList.remove('is-active');
                                    flipItem[i].classList.add('hidden');
                                }, 1000);
                            }, 1500)
                       
                            i = i + n;
                            flipItem[i].classList.remove('is-active');
                            flipItem[i].classList.add('hidden');
                        }, 1500)

                        :
                        flipItem[i].classList.remove('hidden');
                        flipItem[i].classList.add('is-active');
                }

            };
        };
        flipInfoFunction();
    }, [itemToFlip]);



    return (
        <section className="home-section-seven">
            <div className="h-container-7 container">
                <div className="h-section-7-wrap">
                    <h6>why cowrywise?</h6>
                    <div id="disqosWrap" className="relative">
                        {
                            flipItems.map((item) => {
                                return (
                                    <Disqus key={item.title} {...item} />
                                );
                            })
                        }
                    </div>
                </div>
            </div>
        </section>
    );
};
export default HomeSectionSeven;
