import { useState } from "react";
import { flipItems } from "../constants";
import { Disqus } from "../components";
import { useEffect } from "react";



const HomeSectionSeven = () => {

    console.clear();

    const [initialState, setInitialState] = useState(0);

    const [flipItem, setFlipsItem] = useState(flipItems);
    console.log('Flip Items: ', flipItem);
    





    useEffect(() => {
        for (var n = 0; n < flipItem.length; n++) {
            console.log('Active Number: ', n);
            console.log('Active Flip Item: ', flipItem[n]);
            if (initialState !== flipItem[n]) {
                initialState += n++
                console.log('Initial State: ', initialState);
            };

                  
            var disqusID = document.getElementById('disqosWrap');
            var disqosElements = disqusID.getElementsByClassName('disqus');
            console.log('Found Discuss Items: ', disqosElements);
            

            for (var i = 0; i < disqosElements.length; i++) {
                console.log('Found Discuss Items 1: ', disqosElements[i]);

                if (n !== i) {
                    disqosElements[i].classList.remove('is-active');
                    disqosElements[i].classList.add('hidden');
                } else {
                    disqosElements[i].classList.remove('hidden');
                    disqosElements[i].classList.add('is-active');
                };
            }

        };
    }, []);



    return (
        <section className="home-section-seven">
            <div className="h-container-7 container">
                <div className="h-section-7-wrap">
                    <h6>why cowrywise?</h6>
                    <div id="disqosWrap" className="relative">
                        {
                            flipItems.map((item) => {
                                return (
                                    <Disqus key={item.title} {...item} />
                                );
                            })
                        }
                    </div>
                </div>
            </div>
        </section>
    );
};
export default HomeSectionSeven;
