export default function Utils({ isElementInView}) {

    function elementIsInView(element, fullyInView) {
        const pageTop = window.scrollY;
        const pageBottom = pageTop + window.innerHeight;

        const elementTop = element.offsetTop;
        const elementBottom = elementTop + element.offsetHeight;

        if (fullyInView === true) {
            return isElementInView = pageTop < elementTop && pageBottom > elementBottom;
        } else { 
            return isElementInView = elementTop <= pageBottom && elementBottom >= pageTop;
        }
    };
    
   
    return null;
};
